DROP TABLE IF EXISTS beat;
DROP TABLE IF EXISTS player;

CREATE TABLE player ( playerId SERIAL PRIMARY KEY, userName TEXT UNIQUE NOT NULL, playerPass TEXT NOT NULL);

CREATE TABLE beat ( beatId SERIAL PRIMARY KEY, beatName TEXT NOT NULL, beatSequence JSON, player_id INTEGER REFERENCES player(playerId));

